function Emitter(){

  this.events=[];
  this.on = function(eventType, callback){
    var eventObj={
      eventType:eventType,
      callback:callback
    }
    this.events.push(eventObj);
  };

  this.emit = function(eventType){
    eventsArray=this.events.filter(event=>{
      return (eventType===event.eventType);
    })
    eventsArray.forEach(event=>{
      event.callback();
    })
  };

  this.removeListener = function(eventType, callback){
    var indx= this.events.findIndex(event=>{
      return ((event.eventType===eventType)&&(event.callback.toString===callback.toString))
    });
    if (indx > -1){
      this.events.splice(indx,1);
      return true;
    }
    return false;
  };

  this.removeAllListeners= function(eventType){
    this.events = this.events.filter(event=>{
      return event.eventType!==eventType;
    });
  };

}

module.exports=Emitter;